jQuery(document).ready(function( $ ) {
	$('[data-pafe-lightbox-gallery-dark-skin] [data-href]').click(function(){
		var bgColor = $(this).closest('[data-pafe-lightbox-gallery-background-color]').data('pafe-lightbox-gallery-background-color');
    	$('body').removeClass('pafe-lightbox-gallery-light-skin');
    	$(document).find('.pswp__bg').css({'background-color':bgColor});
    });
	$('[data-pafe-lightbox-gallery-light-skin] [data-href]').click(function(){
		var bgColor = $(this).closest('[data-pafe-lightbox-gallery-background-color]').data('pafe-lightbox-gallery-background-color');
    	$('body').addClass('pafe-lightbox-gallery-light-skin');
    	$(document).find('.pswp__bg').css({'background-color':bgColor});
    });
}); 

(function ($) {

	var WidgetPafeMasonry = function ($scope, $) {

        var $grid = $('[data-pafe-masonry]').imagesLoaded( function() {
			$grid.masonry({
				"itemSelector": ".pafe-pswp", "columnWidth": ".pafe-pswp",  "percentPosition": true
			});
		});

    }; 

	$(window).on('elementor/frontend/init', function () {

        elementorFrontend.hooks.addAction('frontend/element_ready/pafe-lightbox-gallery.default', WidgetPafeMasonry);

    });
  
})(jQuery);